mapper.select:
SELECT RQUESTION_NO
FROM RESEARCHQUESTION
WHERE RESEARCH_NO = #{ researchNo }
AND RESEARCH_ORDER = #{ researchOrder }

SELECT MAX(RESEARCHHISTORY_NO)
FROM RESEARCHHISTORY
WHERE MNO = #{ mno }

SELECT RESEARCH_ENGAGEMENT_GOALS
FROM RESEARCH
WHERE RESEARCH_NO = #{ researchNo }

SELECT TERNARY_COUNT
FROM PANELTERNARY
WHERE MNO = #{ mno }

SELECT SURVEY_NO
FROM LIKEHISTORY
WHERE MNO = #{mno}

mapper.update:
UPDATE PANELSURVEY SET
SURVEY_LIKE = (SELECT SURVEY_LIKE FROM PANELSURVEY WHERE SURVEY_NO = #{surveyNo}) - 1
WHERE SURVEY_NO = #{surveyNo}

UPDATE PANELSURVEY SET
SURVEY_LIKE = (SELECT SURVEY_LIKE FROM PANELSURVEY WHERE SURVEY_NO = #{surveyNo}) + 1
WHERE SURVEY_NO = #{surveyNo}

mapper.delete:
DELETE FROM LIKEHISTORY
WHERE SURVEY_NO = #{surveyNo}
AND MNO = #{mno}

mapper.select:
SELECT COUNT(*)
FROM SURVEYHISTORY
WHERE SURVEY_NO = #{surveyNo}
AND MNO = #{mno}
